pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

import "./interfaces/IUtilityBase.sol";
import "./interfaces/ITreasuryDAO.sol";
import "./interfaces/IERC20Token.sol";

contract UTBFactory is Ownable {
	string public constant name = "Utilitybia Factory";

	struct Auction {
		address author;
		uint256 index;
		string asset;
		uint256 price;
		uint256 limit;
		uint256 registeredAt;
		address owner;
	}

	event UtilityAdded(uint256 index, address indexed utility);
	event AuctionCreated(uint256 index, Auction auction);
	event AuctionUpdated(uint256 index, Auction auction);

	IUtilityBase[] public utilities;
	mapping(uint256 => uint8) utilityRegists;
	uint8 public constant withdrawPercent = 5;

	Auction[] public auctions;
	mapping(uint256 => bool) public auctionClaimed;
	uint8 public constant registerPercent = 2;
	uint8 public constant buyoutPercent = 5;

	uint256 public locked;

	address public treasuryDAO;
	IERC20Token public token;

	constructor(address point) {
		token = IERC20Token(point);
	}

	function getToken() public view returns (address) {
		return address(token);
	}

	function totalUtilities() public view returns (uint256 total) {
		total = utilities.length;
	}

	function registerUtility(address utility) public onlyOwner {
		require(utility != address(0), "Utility invalid");

		IUtilityBase newUtility = IUtilityBase(utility);
		require(newUtility.factory() == address(this), "Factory invalid");

		token.addCenter(utility);

		utilities.push(newUtility);
		emit UtilityAdded(utilities.length - 1, utility);
	}

	function setUtilityPoint(uint256 index) public onlyOwner {
		require(index < totalUtilities(), "Utility invalid");
		token.addCenter(address(utilities[index]));
	}

	function removeUtilityPoint(uint256 index) public onlyOwner {
		require(index < totalUtilities(), "Utility invalid");
		token.removeCenter(address(utilities[index]));
	}

	function totalAuctions() public view returns (uint256 total) {
		total = auctions.length;
	}

	function getAuctionTotal(Auction memory auction) internal pure returns (uint256 amount) {
		amount = auction.price * auction.limit;
	}

	function getAuctionFee(Auction memory auction) internal pure returns (uint256 fee) {
		fee = (getAuctionTotal(auction) * registerPercent) / 100;
	}

	function getAuctionRegisterFee(Auction memory auction) internal pure returns (uint256 fee) {
		fee = getAuctionTotal(auction) / 100;
	}

	function getBuyoutFee(Auction memory auction) internal pure returns (uint256 fee) {
		fee = (getAuctionTotal(auction) * buyoutPercent) / 100;
	}

	function getClaimFee(Auction memory auction) internal pure returns (uint256 fee) {
		fee = (getAuctionTotal(auction) * (buyoutPercent - registerPercent)) / 100;
	}

	function toISOPrice(uint256 price) internal view returns (uint256 ISOPrice) {
		uint256 pointRate = token.pointRate();
		ISOPrice = (price / pointRate) * pointRate;
	}

	function registerAuction(
		uint256 index,
		string memory asset,
		uint256 price,
		uint256 limit,
		bool isDirect
	) public payable returns (uint256 auctionIndex) {
		require(index < utilities.length, "Utility invalid");
		require(bytes(asset).length > 0, "Asset invalid");
		require(price > 0, "Price invalid");
		require(limit > 0, "Amount invalid");

		auctionIndex = auctions.length;
		Auction memory newAuction = Auction(
			msg.sender,
			index,
			asset,
			toISOPrice(price),
			limit,
			block.timestamp,
			address(0)
		);

		if (utilities[index].owner() == msg.sender) {
			require(utilityRegists[index] <= 3, "Max limit reached");
			utilityRegists[index] += 1;
		} else {
			if (isDirect) {
				require(msg.value >= getAuctionFee(newAuction), "Fee insufficient");
			} else {
				require(msg.value >= getAuctionRegisterFee(newAuction), "Fee insufficient");
				locked += getAuctionRegisterFee(newAuction);
			}
		}

		if (isDirect) {
			newAuction.owner = msg.sender;
			utilities[index].registerAsset(msg.sender, asset, price, limit);
		}

		auctions.push(newAuction);
		emit AuctionCreated(auctions.length - 1, newAuction);
	}

	modifier onlyPassed(uint256 index) {
		require(index < totalAuctions(), "Auction invalid");
		require(auctions[index].owner != address(0), "Auction not passed");
		_;
	}

	modifier onlyPending(uint256 index) {
		require(index < totalAuctions(), "Auction invalid");
		require(auctions[index].owner == address(0), "Auction passed");
		_;
	}

	modifier onlyAuthor(uint256 index) {
		require(auctions[index].author == msg.sender, "Permission denied");
		_;
	}

	function updateAuction(uint256 index, string memory asset) public onlyPending(index) onlyAuthor(index) {
		require(bytes(asset).length > 0, "Asset invalid");

		auctions[index].asset = asset;
		emit AuctionUpdated(index, auctions[index]);
	}

	function updateAuctionPrice(
		uint256 index,
		uint256 price,
		uint256 limit
	) public onlyPending(index) onlyAuthor(index) {
		require(price > 0, "Price invalid");
		require(limit > 0, "Amount invalid");

		auctions[index].price = toISOPrice(price);
		auctions[index].limit = limit;
		emit AuctionUpdated(index, auctions[index]);
	}

	function bidAuction(uint256 index) public payable onlyPending(index) {
		require(msg.value >= getBuyoutFee(auctions[index]), "Fee insufficient");

		auctions[index].owner = msg.sender;
		locked += getAuctionFee(auctions[index]);
		utilities[auctions[index].index].registerAsset(
			auctions[index].owner,
			auctions[index].asset,
			auctions[index].price,
			auctions[index].limit
		);
		emit AuctionUpdated(index, auctions[index]);
	}

	function claimBid(uint256 index) public onlyPassed(index) onlyAuthor(index) {
		require(!auctionClaimed[index], "Auction already claimed");
		auctionClaimed[index] = true;
		locked -= getClaimFee(auctions[index]);
		payable(msg.sender).transfer(getClaimFee(auctions[index]));
	}

	function raiseFunds() public payable {}

	function setTreasuryDAO(address treasuryDAO_) public onlyOwner {
		require(treasuryDAO_ != address(0), "Treasury DAO invalid");
		treasuryDAO = treasuryDAO_;
	}

	function withdraw() public onlyOwner {
		require(address(this).balance > locked, "Amount insufficient");
		uint256 funds = address(this).balance - locked;
		if (treasuryDAO == address(0)) {
			payable(owner()).transfer(funds);
		} else {
			ITreasuryDAO(treasuryDAO).raise{ value: funds }();
		}
	}
}
